namespace Krialys.Common.Literals;

/// <summary>
/// Represent qualification signal (green, orange and red)
/// </summary>
public static class QualifsLiterals
{
    /// <summary>
    /// Green signal
    /// </summary>
    public const string Green = "data:image/png;base64,";

    /// <summary>
    /// Orange signal
    /// </summary>
    public const string Orange = "data:image/png;base64,";

    /// <summary>
    /// Red signal
    /// </summary>
    public const string Red = "data:image/png;base64,";

    /// <summary>
    /// Stop processus
    /// </summary>
    public const string StopProcessus = "data:image/svg+xml;base64,";

    /// <summary>
    /// Get the corresponding image from value
    /// </summary>
    /// <param name="value">1(green), 2(orange) or 3(red) are accepted</param>
    /// <returns></returns>
    /// <exception cref="InvalidDataException"></exception>
    public static string GetQualifImgSrc(int? value) => value switch
    {
        1 => Green,
        2 => Orange,
        3 => Red,
        _ => throw new InvalidDataException($"Invalid typed value: {value}."),
    };

}
